import { useState } from "react";
import {
Card,
Button,
Progress,
input
} from "@material-tailwind/react";
import uploadIcon from "../static/images/upload-icon.png";
import {
ref,
uploadBytesResumable,
getDownloadURL
} from "firebase/storage";
import { initializeApp } from "firebase/app";
import { getStorage } from "firebase/storage";
import { useHistory } from "react-router-dom";

const app = initializeApp({
apiKey: "YOUR_API_KEY",
authDomain: "YOUR_AUTH_DOMAIN",
projectId: "YOUR_PROJECT_ID",
storageBucket: "YOUR_STORAGE_BUCKET",
messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
appId: "YOUR_APP_ID",
measurementId: "YOUR_MEASUREMENT_ID"
});

const storage = getStorage(app);

function App() {
const [file, setFile] = useState("");
const [percent, setPercent] = useState(0);
const [fileName, setFileName] = useState("");
const [tokenNumber, setTokenNumber] = useState("");
const [isUploaded, setIsUploaded] = useState(false);
const history = useHistory();

function handleChange(event) {
const selectedFile = event.target.files[0];
if (selectedFile.type !== "application/vnd.openxmlformats-officedocument.presentationml.presentation") {
alert("Please select a .pptx file!");
setFile("");
} else {
setFile(selectedFile);
}
}

const handleUpload = () => {
if (!file) {
alert("Please upload the Presentation Slides!");
}

    const storageRef = ref(storage, `/files/${file.name}`);

    // progress can be paused and resumed. It also exposes progress updates.
    // Receives the storage reference and the file to upload.
    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const percent = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );

        // update progress
        setPercent(percent);
      },
      (err) => console.log(err),
      () => {
        // download url
        getDownloadURL(uploadTask.snapshot.ref).then((url) => {
          console.log(url);
          // extract file name from url
          const fileName = decodeURIComponent(url.split("?")[0].split("/").pop());
          console.log("File name:", fileName);
          // extract token number from url
          const token = new URL(url).searchParams.get("token");
          console.log("Token:", token);
        });
      }
    );
  };
  const storageRef = ref(storage, `/files/${file.name}`);
const uploadTask = uploadBytesResumable(storageRef, file);

uploadTask.on(
  "state_changed",
  (snapshot) => {
    const percent = Math.round(
      (snapshot.bytesTransferred / snapshot.totalBytes) * 100
    );
    setPercent(percent);
  },
  (err) => console.log(err),
  () => {
    getDownloadURL(uploadTask.snapshot.ref).then((url) => {
      setFileName(file.name);
      setTokenNumber(url.split("/").slice(-1)[0]);
      setIsUploaded(true);
    });
  }
);


  return (
    <div>


      <Card className="w-9/12 h-96 m-auto">

        <Card className="bg-lightPurple m-6 h-full">
          <p>{percent} % </p>

          <Progress
            value={percent}
            variant="gradient"
            color="deep-purple"
            size="md"
            className="w-full h-9 mb-4 bg-gray-200 rounded-full dark:bg-gray-700 shadow-xl "
          />

          <img src={uploadIcon} alt="" className="w-24 m-auto" onClick={input} />
          <div className="text-center">
          <label htmlFor="pptx-file" className="block text-gray-700 font-bold mb-2">
            Please select a PPTX file:
          </label>

              <input
                type="file"
                id="pptx-file"
                className="mb-8 mx-8"
                onChange={handleChange}
                accept=".pptx"
                style={{
                  backgroundColor: "#F3F4F6",
                  color: "#6B7280",

                  padding: "15px 70px",
                  boxShadow: "none",
                  border: "none",
                  outline: "none",
                  cursor: "pointer",
                }}
              />
          </div>

          <Button
            onClick={handleUpload}
            variant="gradient"
            color="deep-purple"
            size="md"
            className="mb-6 mx-10 rounded-full"
          >

            <span>Upload</span>
          </Button>

        </Card>
      </Card>

    </div>
  );
}

export default App;
