import { useState } from "react";

function App() {
  const [file, setFile] = useState("");
  const [percent, setPercent] = useState(0);
  const [downloadURL, setDownloadURL] = useState("");

  function handleChange(event) {
    setFile(event.target.files[0]);
  }

  const handleUpload = () => {
    if (!file) {
      alert("Please upload the Presentation Slides!");
      return;
    }

    const storageRef = ref(storage, `/files/${file.name}`);

    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const percent = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setPercent(percent);
      },
      (err) => console.log(err),
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then((url) => {
          setDownloadURL(url);
        });
      }
    );
  };

  return (
    <div>
      <Card className="w-9/12 h-96 m-auto">
        <Card className="bg-lightPurple m-6 h-full">
          <p>{percent} %</p>
          <Progress
            value={percent}
            variant="gradient"
            color="deep-purple"
            size="md"
            className="w-full h-7 mb-4 bg-gray-200 rounded-full dark:bg-gray-700 shadow-xl"
          />
          <img
            src={uploadIcon}
            alt=""
            className="w-24 m-auto"
            onClick={input}
          />
          <div className="text-center">
            <label htmlFor="pptx-file" className="block text-gray-700 font-bold mb-2">
              Please select a PPTX file:
            </label>
            <input
              type="file"
              id="pptx-file"
              className="mb-20 mx-8"
              onChange={handleChange}
              accept=".pptx"
              style={{
                backgroundColor: "#F3F4F6",
                color: "#6B7280",
                padding: "10px 20px",
                borderRadius: "9999px",
                boxShadow: "none",
                border: "none",
                outline: "none",
                cursor: "pointer",
              }}
            />
            {downloadURL && (
              <p>
                Download URL: <a href={downloadURL}>{downloadURL}</a>
              </p>
            )}
          </div>
          <Button
            onClick={handleUpload}
            variant="gradient"
            color="deep-purple"
            size="md"
            className="mb-6 mx-10 rounded-full"
          >
            <span>Upload</span>
          </Button>
        </Card>
      </Card>
    </div>
  );
}

export default App;
